function sanitizeHeaderValue(value) {
  // BUG: only trims whitespace
  return value.trim();
}

function isSemver(tag) {
  // BUG: accepts too much
  return /^\d+(\.\d+)+$/.test(tag);
}

function parseCpu(cpu) {
  // BUG: does not handle 'm'
  return Math.round(parseFloat(cpu) * 1000);
}

function safeText(s) {
  // BUG: only replaces '<'
  return s.replaceAll('<','&lt;');
}

function isEqualShallow(a, b) {
  // BUG: misses key count
  for (const k in a) {
    if (a[k] !== b[k]) return false;
  }
  return true;
}
